<?phpnamespace App\Http\Controllers;use App\Config;use Illuminate\Http\Request;class ConfigGeneralController extends Controller{    public function getConfig()    {        $cauhinhs = Config::whereIn('name', ['config-contact','config-company-name', 'config-phone', 'config-email', 'config-address', 'config-name', 'config-introduce','config-seo-keywords','config-seo-title','config-seo-description','config-seo-image'])->orderBy('order','ASC')->get();        return view('backend.admin.config.general', compact('cauhinhs'));    }    public function saveConfig(Request $request)    {        $configCompanyName = $request->input('config-company-name');        $hdconfigCompanyName = $request->input('hd-config-company-name');        $configContact = $request->input('config-contact');        $hdconfigContact = $request->input('hd-config-contact');        $configIntroduce = $request->input('config-introduce');        $hdconfigIntroduce = $request->input('hd-config-introduce');        $configPhone = $request->input('config-phone');        $hdconfigPhone = $request->input('hd-config-phone');        $configEmail = $request->input('config-email');        $hdconfigEmail = $request->input('hd-config-email');        $configAddress = $request->input('config-address');        $hdconfigAddress = $request->input('hd-config-address');        $configName = $request->input('config-name');        $hdconfigName = $request->input('hd-config-name');        $configSeoKeywords=$request->input('seo_keywords');        $hdconfigSeoKeywords=$request->input('hd-seo-keywords');        $configSeoTitle=$request->input('seo_title');        $hdconfigSeoTitle=$request->input('hd-seo-title');        $configSeoDescription=$request->input('seo_description');        $hdconfigSeoDescription=$request->input('hd-seo-description');        $configSeoImage=$request->input('seo-image');        if (strcmp(trim($configSeoKeywords), trim($hdconfigSeoKeywords)) != 0) {            $config = Config::where('name', 'config-seo-keywords')->first();            $config->content = $configSeoKeywords;            $config->save();        }        if (strcmp(trim($configSeoTitle), trim($hdconfigSeoTitle)) != 0) {            $config = Config::where('name', 'config-seo-title')->first();            $config->content = $configSeoTitle;            $config->save();        }        if (strcmp(trim($configSeoDescription), trim($hdconfigSeoDescription)) != 0) {            $config = Config::where('name', 'config-seo-description')->first();            $config->content = $configSeoDescription;            $config->save();        }        if($configSeoImage){            $configSeoImage = substr($configSeoImage, strpos($configSeoImage, 'images'), strlen($configSeoImage) - 1);            $config = Config::where('name', 'config-seo-image')->first();            $config->content = $configSeoImage;            $config->save();        }        if (strcmp(trim($configIntroduce), trim($hdconfigIntroduce)) != 0) {            $config = Config::where('name', 'config-introduce')->first();            $config->content = $configIntroduce;            $config->save();        }        if (strcmp(trim($configContact), trim($hdconfigContact)) != 0) {            $config = Config::where('name', 'config-contact')->first();            $config->content = $configContact;            $config->save();        }        if (strcmp(trim($configCompanyName), trim($hdconfigCompanyName)) != 0) {            $config = Config::where('name', 'config-company-name')->first();            $config->content = $configCompanyName;            $config->save();        }        if (strcmp(trim($configPhone), trim($hdconfigPhone)) != 0) {            $config = Config::where('name', 'config-phone')->first();            $config->content = $configPhone;            $config->save();        }        if (strcmp(trim($configEmail), trim($hdconfigEmail)) != 0) {            $config = Config::where('name', 'config-email')->first();            $config->content = $configEmail;            $config->save();        }        if (strcmp(trim($configAddress), trim($hdconfigAddress)) != 0) {            $config = Config::where('name', 'config-address')->first();            $config->content = $configAddress;            $config->save();        }        if (strcmp(trim($configName), trim($hdconfigName)) != 0) {            $config = Config::where('name', 'config-name')->first();            $config->content = $configName;            $config->save();        }        return redirect()->route('config.general.index')            ->with('success', 'Cấu Hình Lưu Thành Công');    }}